package testing

import (
	"encoding/base64"
	"fmt"
	"github.com/gngpp/vlog"
	"qrcode-token/util/vjson"
	"testing"
)

func TestDecodeBase64(t *testing.T) {
	decodeString, err := base64.StdEncoding.DecodeString("")
	if err != nil {
		vlog.Error(err)
		return
	}
	var m map[string]string
	err = vjson.ByteArrayConvert(decodeString, m)
	fmt.Println(m)
	fmt.Println(string(decodeString))
}
